mapper.select:
/* getAPILogTotalCount */ SELECT COUNT(api_log_id) FROM api_log
${search.searchWord} = #{search.searchValue}
${search.searchWord} LIKE '%' || #{search.searchValue} || '%'
AND status_code >= #{statusCode}::int AND status_code < #{statusCode}::int+100
AND insert_date >= TO_TIMESTAMP(#{search.startDate}, 'YYYYMMDDHH24MISSUS')
AND insert_date <= TO_TIMESTAMP(#{search.endDate}, 'YYYYMMDDHH24MISSUS')

/* getListAPILog */
SELECT * 
FROM api_log


	${search.searchWord} = #{search.searchValue}


	${search.searchWord} LIKE '%' || #{search.searchValue} || '%'



	AND status_code >= #{statusCode}::int AND status_code < #{statusCode}::int+100




	AND insert_date >= TO_TIMESTAMP(#{search.startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{search.endDate}, 'YYYYMMDDHH24MISSUS')





	ORDER BY ${search.orderWord} ${search.orderValue}


	ORDER BY insert_date DESC, api_log_id DESC


OFFSET #{search.offset} LIMIT #{search.limit}

mapper.insert:
		/* insertAPILog */

    		SELECT NEXTVAL('api_log_seq')

		INSERT INTO api_log (
			api_log_id, server_id, server_name, request_ip, user_id, url, status_code, message
		) VALUES (
			#{apiLogId}, #{serverId}, #{serverName}, #{requestIp}, #{userId}, #{url}, #{statusCode}, #{message}
		)
