mapper.select:
/* getCentroidSdo */
SELECT ST_AsText(ST_Centroid(A.geom))
FROM (
	SELECT wkb_geometry as geom
	FROM sk_sdo
	WHERE bjcd = #{bjcd}
	ORDER BY bjcd LIMIT 1
) A

/* getCentroidSgg */
SELECT ST_AsText(ST_Centroid(A.geom))
FROM (
	SELECT wkb_geometry as geom
	FROM sk_sgg
	WHERE bjcd = #{bjcd}
	ORDER BY bjcd LIMIT 1
) A

/* getBridgeTotalCount */ SELECT COUNT(DISTINCT A.ogc_fid) FROM ( SELECT A.ogc_fid, A.fac_sido, A.fac_sgg, A.mng_org, A.brg_nam FROM bridge A LEFT JOIN sk_sdo B ON A.fac_sido = B.name LEFT JOIN sk_sgg C ON A.fac_sgg = C.name
AND B.bjcd LIKE #{sdoCode} || '%'
AND C.bjcd LIKE '%' || #{sggCode} || '%'
AND A.mng_org = #{mngOrg}
AND A.brg_nam LIKE #{brgNam} || '%'
AND EXISTS (SELECT 1 FROM sat WHERE fac_num = A.fac_num)
AND EXISTS (SELECT 1 FROM sensorid WHERE fac_num = A.fac_num)
AND EXISTS (SELECT 1 FROM bridge_drone_file WHERE ogc_fid = A.ogc_fid)
AND A.model = 1
) A

/* getListBridge */ SELECT DISTINCT A.ogc_fid as gid, A.brg_nam, A.end_amd, A.grade, A.brg_nam, ST_X(ST_Centroid(A.wkb_geometry)) AS longitude, ST_Y(ST_Centroid(A.wkb_geometry)) AS latitude FROM bridge A LEFT JOIN sk_sdo B ON A.fac_sido = B.name LEFT JOIN sk_sgg C ON A.fac_sgg = C.name
AND B.bjcd LIKE #{sdoCode} || '%'
AND C.bjcd LIKE '%' || #{sggCode} || '%'
AND A.mng_org = #{mngOrg}
AND A.brg_nam LIKE #{brgNam} || '%'
AND EXISTS (SELECT 1 FROM sat WHERE fac_num = A.fac_num)
AND EXISTS (SELECT 1 FROM sensorid WHERE fac_num = A.fac_num)
AND EXISTS (SELECT 1 FROM bridge_drone_file WHERE ogc_fid = A.ogc_fid)
AND A.model = 1
ORDER BY A.brg_nam OFFSET #{offset} LIMIT #{limit}

/* getListBridgeByBridgeGroupId */
SELECT A.*, ST_X(ST_AsText(ST_Centroid(ST_Transform(A.wkb_geometry, 4326)))) AS longitude, ST_Y(ST_AsText(ST_Centroid(ST_Transform(A.wkb_geometry, 4326)))) AS latitude
FROM bridge A
WHERE A.gru_num = #{bridgeGroupId}

mapper.insert:
/* insetBridge */
SELECT NEXTVAL('bridge_ogc_fid_seq')
INSERT INTO bridge( ogc_fid, fac_num, brg_nam, mng_org, fac_sido, fac_sgg, fac_emd, fac_ri, fac_gra, end_amd, dsn_wet, brg_len, brg_hit, eff_wid, tot_wid, spa_cnt, max_len, tra_cnt, usp_rep, dpi_rep, gru_num, bridge_cm, bridge_lcc, grade, model, insert_dt, wkb_geometry ) VALUES ( #{gid}, #{facNum}, #{brgNam}, #{mngOrg}, #{facSido}, #{facSgg}, #{facEmd}, #{facRi}, #{facGra}, #{endAmd}, #{dsnWet}, #{brgLen}, #{brgHit}, #{effWid}, #{totWid}, #{spaCnt}, #{maxLen}, #{traCnt}, #{uspRep}, #{dpiRep}, #{gruNum}, #{bridgeCm}, #{bridgeLcc}, #{grade}, 0, now(), ST_GeomFromText(#{geom}, 4326) )
